var assert = require('assert');
var cloneArray = require('../');

describe('cloneArray()', function () {
  it('should clone a simple array', function () {
    var a = ['alpha', 'beta', 'gamma'];
    var b = cloneArray(a);
    assert.deepEqual(a, b);
  });

  it('should clone an array with varied keys', function () {
    var a = [0, 'a', {}, [{}], [function() {}], function() {}];
    var b = cloneArray(a);
    assert.deepEqual(a, b);
  });

  it('should clone an array with varied keys', function () {
    var a = [0, 'a', {}, [{}], [function() {}], function() {}];
    var b = cloneArray(a);
    assert.deepEqual(a, b);
    a = a.concat('new');
    assert.notDeepEqual(a, b);
  });
});
