/**
 * ## Clone Object
 *
 * Shallow clone an object.
 *
 * @param  {Object} `obj` The object to clone.
 * @return {Object} The new object.
 */

module.exports = function cloneObject(obj) {
  var keys = Object.keys(obj);
  var len = keys.length;
  var clone = {};

  for (var i = 0; i < len; i++) {
    clone[keys[i]] = obj[keys[i]];
  }
  return clone;
};