var assert = require('assert');
var clone = require('../');

describe('clone()', function () {
  it('should clone a simple array', function () {
    var val = ['alpha', 'beta', 'gamma'];
    assert.deepEqual(clone(val), val);
  });

  it('should clone an array with varied keys', function () {
    var val = [0, 'a', {}, [{}], [function() {}], function() {}];
    assert.deepEqual(clone(val), val);
  });

  it('should clone an object', function () {
    var val = {foo: 'alpha', bar: 'beta', baz: 'gamma'};
    assert.deepEqual(clone(val), val);
  });

  it('should return primitives', function () {
    assert.deepEqual(clone('a'), 'a');
    assert.deepEqual(clone(1), 1);
  });
});